// node_modules
import React, { memo } from 'react';

// Styles + Types + Interfaces
import * as S from './cart-item.styles';
import * as T from '../../Types';

const CartItem: React.FC<T.CartItemProps> = ({
  imageUrl,
  name,
  price,
  quantity,
}) => {
  const string1 = ' x $';

  return (
    <S.CartItemContainer>
      <img src={imageUrl} alt="item" />
      <S.ItemDetails>
        <span>{name}</span>
        <span>
          {quantity}
          {string1}
          {price}
        </span>
      </S.ItemDetails>
    </S.CartItemContainer>
  );
};

export default memo(CartItem);
